import { useState } from 'react';
import './ExpenseForm.css'
const ExpenseForm = (props) => {

    const [inputTitle, setInputTitle] = useState('');
    const [inputAmount, setInputAmount] = useState('');
    const [inputDate, setInputDate] = useState('');

    // const [userInput, setUserInput] = useState({
    //     inputTitle: '',
    //     inputAmount: '',
    //     inputDate: ''
    // })

    const titleChange = (event) => {
        setInputTitle(event.target.value);
        //     setUserInput({
        //     ...userInput,
        //     inputTitle:event.target.value,
        //     })
        // };

        //when the state depends on the prev state
        // setUserInput((prevState) => {
        //     return { ...prevState, inputTitle: event.target.value };
        // });
    };

    const amountChange = (event) => {
        setInputAmount(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     inputAmount: event.target.value,
        // })
    };

    const dateChange = (event) => {
        setInputDate(event.target.value);
        // setUserInput({
        //     ...userInput,
        //     inputDate: event.target.value,
        // })
    };

    const submitHandler = (event) => {
        event.preventDefault();
        const expenseData = {
            title: inputTitle,
            amount: inputAmount,
            date: new Date(inputDate)
        };

        props.onSave(expenseData);

        setInputTitle('');
        setInputAmount('');
        setInputDate('');
    };

    return (
        <form onSubmit={submitHandler}>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label >Title</label>
                    <input type="text" value={inputTitle} onChange={titleChange} />
                </div>
                <div className='new-expense__control'>
                    <label >Amount</label>
                    <input type="number" value={inputAmount} min="0.01" step="0.01" onChange={amountChange} />
                </div>
                <div className='new-expense__control'>
                    <label >Date</label>
                    <input type="date" value={inputDate} min="2019-01-01" max="2022-12-31" onChange={dateChange} />
                </div>
            </div>
            <div className='new-expense__actions'>
                <button type='submit'>Add Expense</button>
            </div>
        </form>
    );
};

export default ExpenseForm;
